INSERT INTO ms_menu (mid, mtitle, craft, taste, diffculty, rtime, ctime, pnum, endp, story, material, acc, step, finall, skill, tid, dianj, time) VALUES
(2, '冰爽苦瓜', '榨汁', '家常味', '新手尝试', '5分钟', '5分钟', '1人份', 'fsserver/ce460fd4a1c18a91a9952a5cf853486c.jpg', '脆嫩爽口，清凉去火。', '[[\"苦瓜500g\",\"\"],[\"枸杞25g\",\"\"]]', '[[\"雪碧1/2大匙(15克)\",\"\"],[\"矿泉水1/2大匙(15克)\",\"\"],[\"橙汁1/2大匙(15克)\",\"\"],[\"冰糖适量\",\"\"],[\"蜂蜜1小匙(3克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将苦瓜洗净去皮，切条,枸杞洗净后备用,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"在碗内放入冰糖、雪碧、矿泉水，冰糖溶化后放入苦瓜片、枸杞，放入冰箱冰镇,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"将冰好的苦瓜装盘，食用时佐以蜂蜜、橙汁。\"]]', 'fsserver/ce460fd4a1c18a91a9952a5cf853486c.jpg', '生苦瓜切片冰镇后，也可以直接蘸东北大酱食用。', 17, 1111111, 1),
(3, '叉烧肉', '榨汁', '辣味', '初级入门', '10分钟', '30分钟', '2人份', 'fsserver/94d8f8183baa09d214e72916333b23aa.jpg', '色泽枣红，甜咸醇香。', '[[\"里脊肉500g\",\"\"],[\"香葱1棵\",\"\"],[\"生姜1块\",\"\"]]', '[[\"食用油500克\",\"\"],[\"酱油2小匙(6克)\",\"\"],[\"料酒3小匙(9克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"白糖1小匙(3克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"葱、姜洗净切末,肉洗净切成长条，放上酱油、盐、料酒、葱、姜腌2 小时,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将腌好的肉沥干后，放入油锅炸成枣红色捞出,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"锅内留少许油，再放入炸好的肉，加入腌制时的汤汁和半碗水，用小火烧半小时左右，加入糖，改用大火，待汁浓时(约小半碗的汁)，将肉盛入盘内，晾凉后，切成薄片装盘，将锅里剩下的汁浇在肉上即可。\"]]', 'fsserver/94d8f8183baa09d214e72916333b23aa.jpg', '切叉烧肉时，一定要逆着肉的纤维方向切，否则肉片不易嚼烂。', 17, 1111110, 2),
(4, '丁香花生', '榨汁', '鲜味', '初中水平', '15分钟', '60分钟', '3人份', 'fsserver/e15c448a5b79e0dc939c8f26510c223d.jpg', '香辣诱人，酥脆爽口。', '[[\"新鲜丁香鱼200g\",\"\"],[\"花生200g\",\"\"],[\"红辣椒1个\",\"\"],[\"青辣椒1个\",\"\"]]', '[[\"食用油500克\",\"\"],[\"酱油1小匙(3克)\",\"\"],[\"料酒1小匙(3克)\",\"\"],[\"鸡粉1小匙(3克)\",\"\"],[\"胡椒粉适量\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"白糖1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将新鲜丁香鱼裹上适量淀粉，放入热油锅中炸约2分钟，至八成酥时捞起沥干油,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将花生炸熟,葱、蒜、辣椒洗净切末,辣椒洗净切末,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"锅底留少量油，爆香葱末、辣椒末、蒜末，加入炸好的小鱼干、花生、调料，用大火拌炒均匀即可。\"]]', 'fsserver/e15c448a5b79e0dc939c8f26510c223d.jpg', '此菜凉食、热食均可。丁香鱼或其他鱼类裹上淀粉后应立即放入油锅炸，如果时间过长，淀粉会在鱼类表面结成颗粒，影响口感。', 17, 1111109, 3),
(5, '多味扁豆', '榨汁', '甜味', '中级掌勺', '30分钟', '数小时', '4人份', 'fsserver/s1534496_152855384135972.jpg', '鲜咸酸甜，香辣可口。', '[[\"扁豆350g\",\"\"],[\"大蒜5瓣\",\"\"]]', '[[\"香油1小匙(3克)\",\"\"],[\"辣椒油1小匙(3克)\",\"\"],[\"酱油1小匙(3克)\",\"\"],[\"麻酱1小匙(3克)\",\"\"],[\"香醋1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"白糖1小匙(3克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"大蒜切末,扁豆洗净后切丝，在沸水中焯熟，以除去毒素和豆腥味,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"扁豆丝捞出后立即投入冷水中浸泡一下，可以保持其碧绿的颜色，口感也更脆嫩,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"然后加入麻酱、酱油、蒜末、辣椒油、精盐、白糖、味精、醋、香油拌匀装盘即可。\"]]', 'fsserver/s1534496_152855384135972.jpg', '扁豆丝焯水的时间不能过长，否则不脆嫩。', 17, 1111108, 4),
(6, '夫妻肺片', '榨汁', '酸味', '高级厨师', '60分钟', '一天', '5人份', 'fsserver/841f164f3390056a02c71b56c0a4320e.jpg', '质嫩鲜美，麻辣香浓。', '[[\"牛肉250g\",\"\"],[\"牛杂250g\",\"\"],[\"盐酥花生仁10g\",\"\"],[\"香葱1棵\",\"\"]]', '[[\"辣椒油1小匙(3克)\",\"\"],[\"酱油1小匙(3克)\",\"\"],[\"料酒1/2大匙(15克)\",\"\"],[\"花椒粉适量\",\"\"],[\"芝麻粉适量\",\"\"],[\"精盐4小匙(12克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"香葱洗净切末,将牛肉、牛杂洗净放入沸水中煮净血水后捞起,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"洗净锅，放入牛肉、牛杂，加入花椒、肉桂、大料、辣椒、精盐、料酒，再加清水，用大火烧开,约30分钟后改用小火煮,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"煮到牛杂熟而不烂时，先熟的先捞出，晾凉待用,将味精、辣椒油、酱油、花椒粉放入碗中调成味汁,将熟花生米拍碎待用,\"],[\"4\",\"fsserver/1_1552059768365.jpeg\",\"再将晾凉的牛肉、牛杂切成薄片混合在一起，淋上调好的味汁拌匀装盘，撒上香葱末、芝麻粉、花生仁末即可。\"]]', 'fsserver/841f164f3390056a02c71b56c0a4320e.jpg', '调味汁时，调料要一次性放足，以免压味。如果在调味料中加上卤水，味道会更地道。', 17, 1111107, 5),
(7, '桂香糯米藕', '榨汁', '香味', '厨神级', '90分钟', '数天', '未知', 'fsserver/86236e25a433956e88ef6c3c0ee40b8e.jpg', '补血润肺，香糯适口。', '[[\"莲藕1节\",\"\"],[\"糯米200g\",\"\"],[\"红枣25g\",\"\"]]', '[[\"红糖11/2大匙(165克)\",\"\"],[\"桂花蜂蜜1大匙(30克)\",\"\"],[\"白糖3小匙(9克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将糯米洗净后用温水浸泡1小时，沥干备用,红枣洗净,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将莲藕刮去外皮，一头连蒂切掉\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"5厘米，留做盖子，将糯米填入莲藕内，用筷子捅结实，加蒂盖，用几根牙签固定封口,\"],[\"4\",\"fsserver/1_1552059768365.jpeg\",\"将酿好的糯米藕放入锅中，加红枣和红糖，注入没过莲藕的清水，大火煮沸后改小火煮30 分钟，捞出糯米藕晾凉,\"],[\"5\",\"fsserver/1_1552059768366.jpeg\",\"将糯米藕切成片，摆入碟中，浇上桂花蜜，撒上白糖即可。\"]]', 'fsserver/86236e25a433956e88ef6c3c0ee40b8e.jpg', '选购莲藕时，应选藕节肥大粗短、表面鲜嫩、不烂不伤不带尾的。藕有池藕与田藕之分。池藕质白嫩，汁多；田藕品质次之。', 24, 1111106, 6),
(8, '花果荠菜', '榨汁', '果味', '新手尝试', '5分钟', '5分钟', '1人份', 'fsserver/17fab55b8d5b7030c77c1b505bb73164.jpg', '色泽清新，口味清淡。', '[[\"荠菜500g\",\"\"],[\"花生米20g\",\"\"],[\"红辣椒1个\",\"\"],[\"黄辣椒1个\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"蚝油1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将荠菜择洗干净，用开水焯后沥干水分、切成末,红、黄辣椒洗净切末,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将花生米炸熟，捞出去皮拍成末,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"将荠菜、花生米、辣椒末放入碗内，加盐、味精、蚝油、香油拌匀装盘即可。 \"]]', 'fsserver/17fab55b8d5b7030c77c1b505bb73164.jpg', '如爱吃酸一点的，可加入少许香醋，味道也非常好。', 17, 1111105, 7),
(9, '酱牛肉', '榨汁', '怪味', '初级入门', '10分钟', '30分钟', '2人份', 'fsserver/e62846f1ee642409e8e3bbf91af73704.jpg', '色泽酱红，内质软烂，口味咸香。', '[[\"牛肉1000g\",\"\"]]', '[[\"葱花\",\"\"],[\"姜末\",\"\"],[\"蒜末\",\"\"],[\"泡红辣椒\",\"\"],[\"醋\",\"\"],[\"精盐\",\"\"],[\"酱油\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"牛肉洗净切大块，肉四周扎小口，均匀地撒上盐，用力揉搓，然后放入盆内腌制(春夏时间短，秋冬时间长一些，但最短不少于12小时，最长不超过48小时)，腌好后用清水洗净，放入开水中煮一下，捞出洗净浮沫。\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"花椒、八角、山奈、肉桂、陈皮、白芷、小茴香装入小布袋，将小布袋放入锅中，加酱油、精盐、葱、姜、白糖和适量的水，烧开后，放入牛肉，待再开时，撇去浮沫，移至小火慢煮，待酱汁浓稠，牛肉熟透，晾凉后，切片装盆。\"]]', 'fsserver/e62846f1ee642409e8e3bbf91af73704.jpg', '切熟牛肉时不能顺着牛肉的纤维丝下刀，否则吃起来不易嚼碎。', 17, 1111104, 8),
(10, '酱香瓜皮', '榨汁', '其他', '初中水平', '15分钟', '60分钟', '3人份', 'fsserver/s9087269_153250857494965.jpg', '咸中带甜，鲜香适口。', '[[\"西瓜皮1000g\",\"\"],[\"水萝卜50g\",\"\"],[\"黄瓜50g\",\"\"],[\"蒜5瓣\",\"\"]]', '[[\"食用油30克\",\"\"],[\"香油2小匙(6克)\",\"\"],[\"酱油2小匙(6克)\",\"\"],[\"料酒2小匙(6克)\",\"\"],[\"精盐2小匙(6克)\",\"\"],[\"白糖1小匙(3克)\",\"\"],[\"味精1小匙(3克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将西瓜外面的翠衣和靠近瓜瓤的白色软层去掉，只留靠近外皮的浅绿色的那层，放太阳下晒至一成干,2 .将晒干的瓜皮洗净，放入沸水中煮一下后捞出晾凉，然后挤干水，切成块状,黄瓜洗净切片,水萝卜洗净切丝,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"锅内倒入食用油，油热后倒入西瓜皮、黄瓜片和水萝卜丝，煸炒几下，加料酒、酱油、白糖、精盐和小半碗水，炒匀后加盖烧几分钟，汤汁将收干时加入味精，淋上香油炒匀即可。 \"]]', 'fsserver/s9087269_153250857494965.jpg', '此菜凉食、热食均可。', 17, 1111103, 9),
(11, '金钩黄瓜条', '榨汁', '家常味', '中级掌勺', '30分钟', '数小时', '4人份', 'fsserver/af0bae990f774ba35836cbcb928b7a48.jpg', '色泽鲜艳，咸香爽口。', '[[\"黄瓜300g\",\"\"],[\"海米10g\",\"\"],[\"香葱1棵\",\"\"]]', '[[\"食用油25克\",\"\"],[\"香油1小匙(3克)\",\"\"],[\"精盐2小匙(6克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将黄瓜洗净去皮，切成条状（可根据个人喜好切成其他形状），加入精盐少许，腌5分钟,将海米洗净，用沸水泡透，捞出沥干水分,香葱洗净切成末,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将黄瓜沥干，加入味精、香油拌匀，再放入海米,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"将炒锅置火上，倒入食用油烧热，下入葱末，炸出香味后捞出不用，把油淋在黄瓜条上，拌匀即可。\"]]', 'fsserver/af0bae990f774ba35836cbcb928b7a48.jpg', '腌黄瓜时不能加太多的盐，否则黄瓜不脆嫩。', 17, 1111102, 10),
(12, '老醋蜇头', '榨汁', '辣味', '高级厨师', '60分钟', '一天', '5人份', 'fsserver/30214c29cd403c90fc4287b754605ac0.jpg', '海蜇头拌好后，放进冰箱冷藏后再吃，口味更好。', '[[\"海蜇头400g\",\"],[\"大蒜3瓣\",\"]]', '[[\"香油2小匙(6克)\",\"],[\"料酒1大匙(30克)\",\"],[\"香醋1小匙(3克)\",\"],[\"精盐1小匙(3克)\",\"],[\"白糖1小匙(3克)\",\"],[\"味精\",\"],[\"1/2小匙(15克)\",\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"醋香蜇脆\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"爽口开胃。\"]]', 'fsserver/30214c29cd403c90fc4287b754605ac0.jpg', '厨房小窍门：', 17, 1111101, 11),
(13, '凉拌蛋皮丝', '榨汁', '鲜味', '厨神级', '90分钟', '数天', '未知', 'fsserver/7abfd1780e8072bec29cd752ba482f53.jpg', '色泽金黄，香嫩可口。', '[[\"鸡蛋2个\",\"\"],[\"榨菜50g\",\"\"],[\"新鲜香菇50g\",\"\"],[\"蒜苗25g\",\"\"]]', '[[\"食用油30克\",\"\"],[\"香油2小匙(6克)\",\"\"],[\"料酒1小匙(3克)\",\"\"],[\"胡椒粉适量\",\"\"],[\"香醋1/2小匙(15克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"白糖1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将鸡蛋打入碗中搅拌，加入盐、胡椒粉、酒及淀粉拌匀，放入热油锅中煎成蛋皮，盛起放凉后切丝,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"蒜苗去头尾，洗净，放沸水中氽烫2 秒钟，迅速捞出，用凉开水冲洗，切细丝,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"香菇洗净后放入沸水中氽烫至熟，捞出，用凉开水冲凉，沥干水后切丝,榨菜洗净，切丝,\"],[\"4\",\"fsserver/1_1552059768365.jpeg\",\"最后将蛋皮丝、香菇丝、榨菜丝、蒜苗丝放入盘内，加入适量醋、糖、香油、盐搅拌均匀即可。\"]]', 'fsserver/7abfd1780e8072bec29cd752ba482f53.jpg', '用料酒将淀粉调匀后再加入蛋汁中，可以避免结块，也不会有蛋腥味。', 17, 1111100, 12),
(14, '凉拌墨鱼丝', '榨汁', '甜味', '新手尝试', '5分钟', '5分钟', '1人份', 'fsserver/cae5371dd39f55a4c03b9367d2799ea0.jpg', '肉质鲜美，爽嫩可口。', '[[\"新鲜墨斗鱼500g\",\"\"],[\"红辣椒1个\",\"\"],[\"青辣椒1个\",\"\"],[\"香葱1棵\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"酱油2小匙(6克)\",\"\"],[\"料酒1大匙(30克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"白糖1小匙(3克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将墨斗鱼去骨、内脏、眼、墨皮后洗净,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"葱洗净切段,鲜姜洗净，切成薄片,青、红辣椒切丝备用,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"往锅里加适量清水，烧开，然后将洗净的墨斗鱼放入沸水中(同时加入料酒、姜片、葱段)。煮透后将墨斗鱼捞出、沥水、晾凉,\"],[\"4\",\"fsserver/1_1552059768365.jpeg\",\"将晾凉的墨斗鱼切成细丝，放入盘内，加入精盐、白糖、酱油腌10 分钟，最后加入味精、香油以及辣椒丝，拌匀即可食用。\"]]', 'fsserver/cae5371dd39f55a4c03b9367d2799ea0.jpg', '水煮墨斗鱼时速度要快，以免肉质变硬。', 17, 1111099, 13),
(15, '凉拌三鲜', '榨汁', '酸味', '初级入门', '10分钟', '30分钟', '2人份', 'fsserver/a0ddb476ee3d425ba26eb4a1c914d94d.jpg', '味道鲜美，清淡爽口。', '[[\"水发海参100g\",\"\"],[\"虾仁100g\",\"\"],[\"熟鸡脯肉100g\",\"\"],[\"红辣椒1个\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"酱油2小匙(6克)\",\"\"],[\"香醋1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将虾仁洗净切片,辣椒洗净切块,水发海参、熟鸡肉分别切成各种形状的片,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"把虾片、海参片分别用沸水焯一下，控干水，和鸡肉片及辣椒片一起放在盘中,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"将所有调料混合调成味汁，浇在虾片、海参片、辣椒片和鸡片上，拌匀即可。\"]]', 'fsserver/a0ddb476ee3d425ba26eb4a1c914d94d.jpg', '采购时不要买用碱发的海参。碱发的海参比水发的海参更油亮，含水量大，弹性小，易碎，烧熟后有股碱味。', 17, 1111098, 14),
(16, '凉爽肉片', '榨汁', '香味', '初中水平', '15分钟', '60分钟', '3人份', 'fsserver/718bbe42da44cbc45eaec3f850a0ef2f.jpg', '肉片鲜嫩，爽口不腻。', '[[\"猪肉(瘦多肥少)500g\",\"\"],[\"韭菜花50g\",\"\"],[\"生姜1小块\",\"\"],[\"大蒜5瓣\",\"\"]]', '[[\"香油1/2小匙(15克)\",\"\"],[\"辣椒油1/2小匙(15克)\",\"\"],[\"酱油2小匙(6克)\",\"\"],[\"料酒1/2大匙(15克)\",\"\"],[\"酱豆腐汁1小匙(3克)\",\"\"],[\"豆豉1小匙(3克)\",\"\"],[\"精盐4小匙(12克)\"', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"姜洗净，一半切片，另一半去皮剁成末,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将猪肉洗净，切成小方块，放入锅中加水，用大火煮开，撇去浮沫，放入姜片、料酒和精盐，改用小火煮，煮至用筷子能戳入肉块即可，捞出，晾凉,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"将大蒜洗净，捣成蒜泥，放入碗内,\"],[\"4\",\"fsserver/1_1552059768365.jpeg\",\"将韭菜花洗净，沥水后捣烂，放入装蒜泥的碗内，再加入酱油、酱豆腐汁、豆豉、白糖、辣椒油、香油、味精拌匀待用,\"],[\"5\",\"fsserver/1_1552059768366.jpeg\",\"将晾凉的肉块上的过多的肥肉去掉，只留2分厚的肥肉,将肉块切成薄片，肥肉朝上码在盘内，淋上调料即可。\"]]', 'fsserver/718bbe42da44cbc45eaec3f850a0ef2f.jpg', '猪肉可以不用完全煮熟，八成熟最佳。', 17, 1111097, 15),
(17, '卤香鸡杂', '榨汁', '果味', '中级掌勺', '30分钟', '数小时', '4人份', 'fsserver/8befc7d628545dec16f449b7d3c309e3.jpg', '卤汁浓香，色泽红亮。', '[[\"鸡肫200g\",\"\"],[\"鸡肝200g\",\"\"],[\"香葱1棵\",\"\"],[\"生姜1小块\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"酱油2小匙(6克)\",\"\"],[\"精盐4小匙(12克)\",\"\"],[\"白糖1小匙(3克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将鸡肫、鸡肝分别用开水烫一下，去除血水,葱洗净切段，姜去皮切片,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"把鸡肫、大料、葱段、姜片、盐、酱油、白糖放入锅内，加适量清水烧开，煮1 小时，再放入鸡肝煮半小时，改小火，然后浇入香油，烧浓卤汁，约烧10分钟，把鸡杂、卤汁一同倒入盆内晾凉即可。\"]]', 'fsserver/8befc7d628545dec16f449b7d3c309e3.jpg', '鸡杂一定要洗干净，否则腥味太重。', 17, 1111096, 16),
(18, '麻辣耳丝', '榨汁', '怪味', '高级厨师', '60分钟', '一天', '5人份', 'fsserver/a4b47c7482f46dca7b5c746da852be85.jpg', '鲜香可口，麻辣开胃。', '[[\"猪耳朵400g\",\"\"],[\"香葱1棵\",\"\"],[\"花椒4g\",\"\"],[\"干辣椒4g\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"精盐4小匙(12克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"先将猪耳朵刮洗干净，抹上盐，腌3 小时，再放入锅中，大火煮至六七成熟，捞出晾凉,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"把猪耳朵切成丝，加入盐和味精，用手拌成蓬松状，放入盘内,香葱洗净切丝,干辣椒切碎,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"把香油倒入锅内，烧至七成热，将花椒和辣椒末倒入，用小火炸3 分钟，再转用中火，待花椒炸出香味时，捞出花椒、辣椒，把香油淋在猪耳丝上，撒上葱丝即可。\"]]', 'fsserver/a4b47c7482f46dca7b5c746da852be85.jpg', '如果将猪耳中的脆骨取出，单独凉拌，口感更佳。', 17, 1111095, 17),
(19, '毛蛤拌菠菜', '榨汁', '其他', '厨神级', '90分钟', '数天', '未知', 'fsserver/s3879176_153119632436985.jpg', '味道鲜美，清淡爽口。', '[[\"毛蛤250g\",\"\"],[\"菠菜250g\",\"\"],[\"生姜1小块\",\"\"],[\"大蒜4瓣\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"香醋1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将毛蛤煮熟取肉，姜、蒜洗净后切末,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"菠菜择洗干净，焯水过凉，沥干水后切成段,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"将毛蛤、菠菜、盐、醋、味精、香油、姜末、蒜末拌匀，装盘即可。\"]]', 'fsserver/s3879176_153119632436985.jpg', '调拌时可先拌毛蛤再拌菠菜，然后再一起调拌。', 17, 1111094, 18),
(20, '清凉梨丝', '榨汁', '家常味', '新手尝试', '5分钟', '5分钟', '1人份', 'fsserver/9cdfc49460f81c52462df0d64eaf174c.jpg', '酸甜脆嫩，清凉消暑。', '[[\"鸭梨300g\",\"\"],[\"山楂糕50g\",\"\"]]', '[[\"香醋1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"白糖1小匙(3克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将梨洗净削皮、去籽，切成丝,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将山楂糕切成菱形片，摆在盘底,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"将梨丝加盐、白糖、醋拌匀，放在山楂片上即可。\"]]', 'fsserver/9cdfc49460f81c52462df0d64eaf174c.jpg', '为了防止梨丝氧化，可用调料将梨丝拌匀后再装盘。', 17, 1111093, 19),
(21, '三色蒸蛋', '榨汁', '辣味', '初级入门', '10分钟', '30分钟', '2人份', 'fsserver/e494cc455c0e2dcbb8d95c3fb89a2383.jpg', '颜色分明，口感极佳。', '[[\"鸡蛋3个\",\"\"],[\"皮蛋3个\",\"\"],[\"熟鸭蛋3个\",\"\"]]', '[[\"猪油2小匙(6克)\",\"\"],[\"香油2小匙(6克)\",\"\"],[\"高汤1/2大匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将鸡蛋打入碗中搅拌均匀，加入猪油和高汤，充分调匀备用,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将皮蛋、熟鸭蛋去壳后切小块，放入打匀的蛋汁中略拌一下，放入蒸锅，用中火蒸20 分钟，取出,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"待蒸蛋冷却后倒扣出来切薄片，盛在盘中，淋上香油即可。(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)\"]]', 'fsserver/e494cc455c0e2dcbb8d95c3fb89a2383.jpg', '蒸蛋时要掌握好火候，以免蒸蛋变硬。', 24, 1111092, 20),
(22, '三丝鱼皮', '榨汁', '鲜味', '初中水平', '15分钟', '60分钟', '3人份', 'fsserver/90923b145ead5caed1f2e4f5ae256cdb.jpg', '色泽美观，口感爽脆。', '[[\"水发鱼皮400g\",\"\"],[\"红辣椒1个\",\"\"],[\"青辣椒1个\",\"\"],[\"黄辣椒1个\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"芥末油适量\",\"\"],[\"料酒1大匙(30克)\",\"\"],[\"胡椒粉1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将鱼皮、辣椒洗净切丝,蒜洗净切片,香菜洗净切段,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"碗内放入鱼皮丝、蒜片、辣椒丝，加入所有调料拌匀，装盘即可。\"]]', 'fsserver/90923b145ead5caed1f2e4f5ae256cdb.jpg', '购买时要注意辨别，不要买用碱发的鱼皮。碱发的鱼皮颜色光亮，含水量大，弹性小，烧制时容易出水，并且口感不好，有股碱味。', 17, 1111091, 21),
(23, '珊瑚藕片', '榨汁', '甜味', '中级掌勺', '30分钟', '数小时', '4人份', 'fsserver/6aff4665bc48eafba53584019104864d.jpg', '酸甜味佳，红白相间。', '[[\"莲藕500g\",\"\"],[\"香葱1棵\",\"\"],[\"干辣椒4个\",\"\"]]', '[[\"香油2/3大匙(20克)\",\"\"],[\"香醋2/3大匙(20克)\",\"\"],[\"白糖11/2大匙(165克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将藕洗净削皮，一剖两半，再切成薄片，在凉水中浸泡一会,葱洗净切丝,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"把藕片放入沸水中烫一下，捞出，用冷水浸凉，沥干水后放入盘内,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"将2 个干辣椒切丝后与葱丝一并放入藕盘内，再将醋、白糖调匀，待糖溶化后浇在藕片上,\"],[\"4\",\"fsserver/1_1552059768365.jpeg\",\"往锅里加香油烧热，把剩下的干辣椒切段投入锅内，炸出辣味后浇在藕上，加盖略焖即可。\"]]', 'fsserver/6aff4665bc48eafba53584019104864d.jpg', '最好用鲜藕，不要用袋装藕或罐装藕；藕片用热水反复泡洗后口感更佳。', 17, 1111090, 22),
(24, '爽口鱼片', '榨汁', '酸味', '高级厨师', '60分钟', '一天', '5人份', 'fsserver/90923b145ead5caed1f2e4f5ae256cdb.jpg', '姜汁浓郁，淡雅爽口。', '[[\"鲩鱼500g\",\"\"],[\"红辣椒1个\",\"\"],[\"泡椒4g\",\"\"],[\"泡萝卜4g\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"高汤1/2大匙(15克)\",\"\"],[\"香醋1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将泡萝卜、泡椒、辣椒洗净后分别切成丁，均匀地垫在盘底,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"姜洗净切末后加盐、醋浸泡片刻，与高汤、味精、香油调成姜汁,香菜切段备用,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"将鱼宰杀洗净，把鱼肉切成片，放入沸水中氽熟，捞出码在已垫底料的盘上，淋上姜汁拌匀，撒上香菜即可。注：鲩鱼即草鱼。(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)\"]]', 'fsserver/90923b145ead5caed1f2e4f5ae256cdb.jpg', '鱼肉切片后用凉水浸泡一会，口感会更好。', 17, 1111089, 23),
(25, '素拌什锦', '榨汁', '香味', '厨神级', '90分钟', '数天', '未知', 'fsserver/d69c26bf52edeb121670e3288dbce81d.jpg', '口味清淡，色泽鲜明。', '[[\"魔芋300g\",\"\"],[\"黄豆芽50g\",\"\"],[\"芹菜25g\",\"\"],[\"竹笋25g\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"辣椒油1小匙(3克)\",\"\"],[\"香醋1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"白糖1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将魔芋去皮洗净后切丝,黄豆芽洗净，沥干水,芹菜洗净切段,竹笋、木耳、胡萝卜分别洗净切丝,金针菇洗净，切去尾端并撕开,干辣椒洗净,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将处理好的材料放沸水中烫熟，一起放入盘中，再加入所有的调味料拌匀即可。\"]]', 'fsserver/d69c26bf52edeb121670e3288dbce81d.jpg', '可以根据个人口味选用不同的素菜调拌。', 24, 1111088, 24),
(26, '蓑衣黄瓜', '榨汁', '果味', '新手尝试', '5分钟', '5分钟', '1人份', 'fsserver/f3e762e10cd75badbde4147715513191.jpg', '清淡爽口，酸甜稍辣。', '[[\"黄瓜250g\",\"\"],[\"香菇25g\",\"\"],[\"胡萝卜25g\",\"\"],[\"冬笋25g\",\"\"]]', '[[\"食用油30克\",\"\"],[\"香醋1小匙(3克)\",\"\"],[\"精盐3小匙(9克)\",\"\"],[\"白糖1/2小匙(15克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将黄瓜洗净，切成蓑衣花刀，用盐腌10 分钟，再用清水冲洗后沥干水分装盘,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将香菇、胡萝卜、冬笋、葱、姜洗净切丝,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"锅内放油，油烧至六成热时放入葱丝、姜丝，炒出香味后再倒入香菇丝、胡萝卜丝、冬笋丝翻炒，加入白糖、醋、精盐、味精，烧开,\"],[\"4\",\"fsserver/1_1552059768365.jpeg\",\"将糖醋汁放凉后倒入装黄瓜的盘中，浸泡几小时后即可食用。\"]]', 'fsserver/f3e762e10cd75badbde4147715513191.jpg', '必须等糖醋汁凉透后再浸泡黄瓜，这样黄瓜会更脆爽。', 17, 1111087, 25),
(27, '糖醋三丝', '榨汁', '怪味', '初级入门', '10分钟', '30分钟', '2人份', 'fsserver/e494cc455c0e2dcbb8d95c3fb89a2383.jpg', '甜酸清爽，解腻开胃。', '[[\"白菜心200g\",\"\"],[\"鸭梨300g\",\"\"],[\"山楂糕50g\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"白糖2小匙(6克)\",\"\"],[\"香醋2小匙(6克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将白菜心洗净，切成丝，用精盐拌匀腌一下,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"鸭梨去皮核，切丝,山楂糕切成粗丝,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"沥去白菜丝的水分，放入盘内，将梨丝码在白菜丝上，最上面放山楂丝,\"],[\"4\",\"fsserver/1_1552059768365.jpeg\",\"白糖和醋加上少许清水后倒入热锅内熬化，倒出晾凉后浇在三丝上，淋上香油即可。 \"]]', 'fsserver/e494cc455c0e2dcbb8d95c3fb89a2383.jpg', '三丝不要切得太细，否则会影响口感。', 17, 1111086, 26),
(28, '虾皮拌豇豆', '榨汁', '其他', '初中水平', '15分钟', '60分钟', '3人份', 'fsserver/023e288ba7bc144f271c90d32e973424_150x150.jpg', '色泽碧绿，鲜香爽口。', '[[\"嫩豇豆200g\",\"\"],[\"虾皮50g\",\"\"],[\"红辣椒1个\",\"\"],[\"黄辣椒1个\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"香醋1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"把豇豆洗净切段，用沸水焯透，沥干水分装盘,葱、姜洗净切末,红、黄辣椒切丝,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"虾皮洗净，沥干水分，放在豇豆上，再放上葱、姜、调料拌匀，淋香油即成。\"]]', 'fsserver/023e288ba7bc144f271c90d32e973424_150x150.jpg', '虾皮沥干水后，用油炸一下再拌也可以。', 17, 1111085, 27),
(29, '鲜辣松花蛋', '榨汁', '家常味', '中级掌勺', '30分钟', '数小时', '4人份', 'fsserver/b6bd84f1e82fda0c5714d349aeca60e4.jpg', '味美汁鲜，开胃下酒。', '[[\"新鲜香菇300g\",\"\"],[\"皮蛋4个\",\"\"],[\"红辣椒1个\",\"\"],[\"青辣椒1个\",\"\"]]', '[[\"食用油30克\",\"\"],[\"香油2小匙(6克)\",\"\"],[\"酱油1小匙(3克)\",\"\"],[\"白糖1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"香菇洗净、去蒂，每朵对切成4 等份后沥干水分，再放锅内干炒2 分钟后盛出,蒜洗净切末,辣椒洗净切成环状,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"再往锅里加入适量食用油，烧热后下蒜末、辣椒煸炒几下，然后放入香菇，先用大火炒，再改小火慢慢煨，直到香菇不再出水，微呈干缩状时关火，盛盘,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"将皮蛋剥壳、切片，放入盛香菇的盘内，放入酱油、糖、香油调匀即可。 \"]]', 'fsserver/b6bd84f1e82fda0c5714d349aeca60e4.jpg', '香菇的水分是否已去除，可根据油中是否仍有水珠冒出来判断，但火不能大，以免把香菇炸得太干。', 17, 1111084, 28),
(30, '鲜香紫茄', '榨汁', '辣味', '高级厨师', '60分钟', '一天', '5人份', 'fsserver/a4152544_153794235872674.jpg', '鲜嫩爽口，脆咸生香。', '[[\"紫皮茄子300g\",\"\"],[\"大蒜4瓣\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"甜面酱1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将茄子洗净，带皮切成块状，放入沸水中氽一下，捞出沥干水,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"把大蒜洗净捣成茸，放入茄子中，另放入精盐、甜面酱、味精、香油拌匀即可食用。\"]]', 'fsserver/a4152544_153794235872674.jpg', '茄子焯水时可在水里加入少量盐、糖，这样既可保持茄子的本色，又可以使茄子更入味。', 17, 1111083, 29),
(31, '香椿拌鸡丝', '榨汁', '鲜味', '厨神级', '90分钟', '数天', '未知', 'fsserver/90b39ba7b61bffa3cd19e2deb50536d7.jpg', '色泽淡雅，清香味美。', '[[\"鸡脯肉200g\",\"\"],[\"椿芽100g\",\"\"]]', '[[\"香油2小匙(6克)\",\"\"],[\"酱油1小匙(3克)\",\"\"],[\"芝麻酱1小匙(3克)\",\"\"],[\"香醋1/2小匙(15克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将鸡肉切成细丝，放入沸水中氽熟，捞出备用,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将椿芽洗净，放入沸水中烫一下，迅速捞出沥水，切成段,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"在鸡丝上撒匀椿芽段，再加入精盐、酱油、醋、芝麻酱、香油、味精拌匀即可。\"]]', 'fsserver/90b39ba7b61bffa3cd19e2deb50536d7.jpg', '椿芽焯水后必须马上拨开晾凉，否则椿芽容易变黄。', 17, 1111082, 30),
(32, '香油海带丝', '榨汁', '甜味', '新手尝试', '5分钟', '5分钟', '1人份', 'fsserver/4e24838f86db7b993bb7c23e74c79aaf.jpg', '鲜咸适口，清香脆嫩。', '[[\"水发海带300g\",\"\"],[\"香葱2棵\",\"\"],[\"生姜1小块\",\"\"]]', '[[\"食用油10克\",\"\"],[\"香油1小匙(3克)\",\"\"],[\"酱油1小匙(3克)\",\"\"],[\"胡椒粉适量\",\"\"],[\"香醋1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"白糖1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将海带洗净，切成细丝，放入沸水中焯过，捞出沥水装盘,葱、姜洗净切丝,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"将酱油、精盐、白糖、醋、胡椒粉、香油、味精调匀后浇在海带丝上，撒上葱姜丝,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"把食用油烧热，浇在海带丝上拌匀即可。\"]]', 'fsserver/4e24838f86db7b993bb7c23e74c79aaf.jpg', '如果海带丝有点硬，可用肉汤多煨一会。', 17, 1111081, 31),
(33, '油泼螺片', '榨汁', '酸味', '初级入门', '10分钟', '30分钟', '2人份', 'fsserver/s6482563_151295890065372.jpg', '肉质鲜嫩，香味浓郁。', '[[\"海螺500g\",\"\"],[\"大蒜3瓣\",\"\"],[\"香葱1棵\",\"\"],[\"生姜1小块\",\"\"]]', '[[\"花生油10克\",\"\"],[\"蚝油2小匙(6克)\",\"\"],[\"酱油2小匙(6克)\",\"\"],[\"香醋1小匙(3克)\",\"\"],[\"精盐1小匙(3克)\",\"\"],[\"味精1/2小匙(15克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"将海螺敲破取肉，用醋洗去黏液，用刀将海螺片成薄片，放入沸水中焯过，装入盘中。香菜洗净切段,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"先将部分葱、姜和蒜洗净后切成末，用蚝油、酱油调成汁,再将剩余葱和姜切丝备用,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"将调好的汁倒在螺片上，撒上香菜段和葱、姜丝，浇热油即可。\"]]', 'fsserver/s6482563_151295890065372.jpg', '这道菜热吃凉吃都行。海螺肉焯水时一定要快，否则肉质会变老。', 17, 1111080, 32),
(34, '鲍菇蚕豆汤', '榨汁', '香味', '初中水平', '15分钟', '60分钟', '3人份', 'fsserver/75ef284e7adcd5c624be049bb4e72155.jpg', '滑爽可口，汤汁鲜美。', '[[\"鲍鱼菇250g\",\"\"],[\"蚕豆50g\",\"\"],[\"银耳10g\",\"\"]]', '[[\"食用油30克\",\"\"],[\"香油1小匙(3克)\",\"\"],[\"高汤3大匙(90克)\",\"\"],[\"精盐5克\",\"\"],[\"白糖3克\",\"\"],[\"味精5克\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"银耳用清水泡发，摘掉硬黄部分，撕碎,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"鲍鱼菇洗净切片，蚕豆洗净,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"把高汤与鲍鱼菇片同放入锅内，煮开,\"],[\"4\",\"fsserver/1_1552059768365.jpeg\",\"下蚕豆、精盐煮熟，加银耳略煮,\"],[\"5\",\"fsserver/1_1552059768366.jpeg\",\"用胡椒粉调味即可。(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)\"]]', 'fsserver/75ef284e7adcd5c624be049bb4e72155.jpg', '银耳不要煮太长时间，否则口感不脆。', 17, 1111079, 33),
(35, '菠菜干贝汤', '榨汁', '果味', '中级掌勺', '30分钟', '数小时', '4人份', 'fsserver/718bbe42da44cbc45eaec3f850a0ef2f.jpg', '色泽清新，味淡鲜美。', '[[\"干贝10g\",\"\"],[\"菠菜250g\",\"\"],[\"鸡蛋2个\",\"\"],[\"淀粉4g\",\"\"]]', '[[\"食用油20克\",\"\"],[\"高汤15大匙(450克)\",\"\"],[\"胡椒粉1小匙(3克)\",\"\"],[\"精盐2小匙(6克)\",\"\"],[\"味精1小匙(3克)\",\"\"]]', '[[\"1\",\"fsserver/endp_1552059768360.jpeg\",\"干贝洗净，放碗内加料酒、清水浸泡30分钟,菠菜洗净，撕开,鸡蛋只取蛋清,\"],[\"2\",\"fsserver/1_1552059768363.jpeg\",\"干贝上蒸锅蒸30 分钟后取出，揉散，汤汁留用,\"],[\"3\",\"fsserver/1_1552059768364.jpeg\",\"锅内倒入油，烧热，慢慢倒入蛋清，煎成蛋花盛出,\"],[\"4\",\"fsserver/1_1552059768365.jpeg\",\"烧沸蒸干贝的汤，下入干贝、精盐、胡椒粉、蛋花，淋入鸡油，倒入菠菜煮熟，用水淀粉勾芡即可。\"]]', 'fsserver/718bbe42da44cbc45eaec3f850a0ef2f.jpg', '蒸干贝时如果放入少许葱姜，干贝的味道会更加鲜美。', 17, 1111078, 34),
